function submitScreenTrollAjax(t,e){return $.ajax({type:"POST",url:"/tools/cgi-bin/screenTroll/screenTrollOutput.cgi",data:{outputContents:$("#output").html()},dataType:"html",beforeSend:function(){$("#"+e).show()},success:function(e){$("#"+t).html(e),$("#"+t).slideDown()},error:function(){var e='<div class="alert">Sorry, an error occurred. Please try again or contact the admin.</div>';$("#"+t).html(e)},complete:function(){$(".loading").hide()}}),!1}